import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useCookies } from 'react-cookie';
import { useNavigate } from 'react-router-dom';
import './UserPage.css';

function UserPage() {
  const [userData, setUserData] = useState(null);
  const [cookies, removeCookie] = useCookies(['email', 'password']);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      if (!cookies.email || !cookies.password) {
        navigate('/login');
      } else {
        try {
          // Fetch user authentication data
          const authToken = `${cookies.email}|${cookies.password}`;
          const authResponse = await axios.post(
            'https://9xna35s7vi.execute-api.eu-central-1.amazonaws.com/dev/user/getAuth',
            {
              "authorizationToken": authToken 
            }
          );

          // Fetch user data
          const response = await axios.post(
            'https://9xna35s7vi.execute-api.eu-central-1.amazonaws.com/dev/user/readitem',
            {
              "IDs": [authResponse.data.ID]
            },
            {
              headers: {
                'Authorization': authToken
              }
            }
          );
          setUserData(response.data);
          console.log(response.data)
        } catch (error) {
          console.error('Error:', error);
        }
      }
    };

    fetchData();
  }, []);

  const handleLogout = () => {
    removeCookie('email');
    removeCookie('password');
    navigate('/login');
  };

  return (
    <div className="UserPage">
      {userData ? (
        <>
          <h1>Welcome {userData.Vorname}!</h1>
          <button onClick={handleLogout}>Logout</button>
          <div className="userData">
            <div className="field">
              <div className="label">Name:</div>
              <div className="value">{userData.Name}</div>
            </div>
            <div className="field">
              <div className="label">Vorname:</div>
              <div className="value">{userData.Vorname}</div>
            </div>
            <div className="field">
              <div className="label">Mail:</div>
              <div className="value">{userData.Mail}</div>
            </div>
            {/* Repeat this pattern for each field */}
          </div>
        </>
      ) : (
        <p>Please log in</p>
      )}
    </div>
  );
}

export default UserPage;
