import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useCookies } from 'react-cookie';
import { useHistory } from 'react-router-dom';

function UserPage() {
  const [userData, setUserData] = useState({});
  const [cookies, setCookie, removeCookie] = useCookies(['email', 'password']);
  const history = useHistory();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const authToken = `${cookies.email}|${cookies.password}`;

        const authResponse = await axios.post(
          'https://9xna35s7vi.execute-api.eu-central-1.amazonaws.com/dev/user/getAuth',
          {
            "authorizationToken": authToken 
          }
        );

        const response = await axios.post(
          'https://9xna35s7vi.execute-api.eu-central-1.amazonaws.com/dev/user/readitem',
          {
            "IDs": [authResponse.data.ID]
          },
          {
            headers: {
              'Authorization': authToken
            }
          }
        );

        setUserData(response.data);
      } catch (error) {
        console.error('Error:', error);
      }
    };

    fetchData();
  }, [cookies.email, cookies.password]);

  const handleLogout = () => {
    removeCookie('email');
    removeCookie('password');
    history.push('/login'); // Redirect to login page after logout
  };

  return (
    <div>
      <h1>User Data</h1>
      <ul>
        {Object.entries(userData).map(([key, value]) => (
          <li key={key}>
            <strong>{key}:</strong> {JSON.stringify(value)}
          </li>
        ))}
      </ul>
      <button onClick={handleLogout}>Logout</button>
    </div>
  );
}

export default UserPage;
