import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Admin.css';

function Admin() {
  const navigate = useNavigate();
  const [plz, setPlz] = useState('');
  const [authorization, setAuthorization] = useState('');
  const [responseData, setResponseData] = useState([]);
  const [idToken, setIdToken] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  // Function to handle login
  const handleLogin = async () => {
    try {
      const response = await axios.post(
        'https://vaccin-userpool.auth.eu-central-1.amazoncognito.com/oauth2/authorize?client_id=4lml12ccu9en7bj5o56jbh1dvf&response_type=code&scope=email+openid&redirect_uri=https%3A%2F%2Funi-bamberg.de',
        `grant_type=password&client_id=4lml12ccu9en7bj5o56jbh1dvf&username=${encodeURIComponent(email)}&password=${encodeURIComponent(password)}`,
        {
          headers: {
            'Content-Type': 'application/x-www-form-urlencoded',
          },
        }
      );
      setIdToken(response.data.id_token);
    } catch (error) {
      console.error('Error logging in:', error);
    }
  };

  useEffect(() => {
    const getIdTokenFromUrl = () => {
      const params = new URLSearchParams(window.location.hash.substring(1));
      const idTokenFromUrl = params.get('id_token');
      setIdToken(idTokenFromUrl);
    };

    getIdTokenFromUrl();
  }, []);

  const handleAdminClick = () => {
    navigate('/admin');
  };

  const handleCreateTable = async () => {
    try {
      const response = await axios.post(
        'https://9xna35s7vi.execute-api.eu-central-1.amazonaws.com/dev/admin/getuserbyzipcode',
        {
          "PLZ": plz
        },
        {
          headers: {
            'Authorization': authorization
          }
        }
      );
      setResponseData(response.data);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  return (
    <div className="Admin">
      <h1>Welcome to the Admin Page</h1>
      <button onClick={handleAdminClick}>Go to Admin Page</button>
      <div className="input-container">
        <input
          type="text"
          placeholder="Auth Token"
          value={authorization}
          onChange={(e) => setAuthorization(e.target.value)}
        />
        <input
          type="text"
          placeholder="PLZ"
          value={plz}
          onChange={(e) => setPlz(e.target.value)}
        />
        <button onClick={handleCreateTable}>Fetch Data</button>
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button onClick={handleLogin}>Login</button>
      </div>
      <p>id_token from URL: {idToken}</p>
      <table className="data-table">
        <thead>
          <tr>
            {Object.keys(responseData[0] || {}).map((key) => (
              <th key={key} scope="col">{key}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {responseData.map((item, index) => (
            <tr key={index}>
              {Object.values(item).map((value, index) => (
                <td key={index}>{value}</td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Admin;
