import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Admin.css';

function Admin() {
  const navigate = useNavigate();
  const [plz, setPlz] = useState('');
  const [authorization, setAuthorization] = useState('');
  const [responseData, setResponseData] = useState([]);
  const [idToken, setIdToken] = useState('');

  // Function to handle login
  const handleLogin = () => {
    const clientId = '4lml12ccu9en7bj5o56jbh1dvf';
    const redirectUri = encodeURIComponent('https://uni-bamberg.de');
    const authEndpoint = `https://vaccin-userpool.auth.eu-central-1.amazoncognito.com/oauth2/authorize?client_id=${clientId}&response_type=token&scope=email+openid&redirect_uri=${redirectUri}`;
    window.location.href = authEndpoint;
  };

  useEffect(() => {
    // Function to extract id_token from URL
    const getIdTokenFromUrl = () => {
      const params = new URLSearchParams(window.location.hash.substring(1));
      const idTokenFromUrl = params.get('id_token');
      setIdToken(idTokenFromUrl);
    };

    getIdTokenFromUrl(); // Call the function when the component mounts
  }, []);

  const handleAdminClick = () => {
    navigate('/admin');
  };

  const handleCreateTable = async () => {
    try {
      const response = await axios.post(
        'https://9xna35s7vi.execute-api.eu-central-1.amazonaws.com/dev/admin/getuserbyzipcode',
        {
          "PLZ": plz
        },
        {
          headers: {
            'Authorization': authorization
          }
        }
      );
      setResponseData(response.data);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  return (
    <div className="Admin">
      <h1>Welcome to the Admin Page</h1>
      <button onClick={handleAdminClick}>Go to Admin Page</button>
      <div className="input-container">
        <input
          type="text"
          placeholder="Auth Token"
          value={authorization}
          onChange={(e) => setAuthorization(e.target.value)}
        />
        <input
          type="text"
          placeholder="PLZ"
          value={plz}
          onChange={(e) => setPlz(e.target.value)}
        />
        <button onClick={handleCreateTable}>Fetch Data</button>
        <button onClick={handleLogin}>Login</button> {/* Add a login button */}
      </div>
      <p>id_token from URL: {idToken}</p>
      <table className="data-table">
        <thead>
          <tr>
            {Object.keys(responseData[0] || {}).map((key) => (
              <th key={key} scope="col">{key}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {responseData.map((item, index) => (
            <tr key={index}>
              {Object.values(item).map((value, index) => (
                <td key={index}>{value}</td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Admin;
