import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useCookies } from 'react-cookie'; // Import useCookies hook

function UserPage() {
  const [userData, setUserData] = useState(null); // State to store user data
  const [cookies] = useCookies(['email', 'password']); // Get email and password from cookies

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Create authToken using email and password from cookies
        const authToken = `${cookies.email}|${cookies.password}`;

        // Make the API call to fetch user data
        const authResponse = await axios.post(
          'https://9xna35s7vi.execute-api.eu-central-1.amazonaws.com/dev/user/getAuth',
          {
            "authorizationToken": authToken 
          }
        );

        const response = await axios.post(
          'https://9xna35s7vi.execute-api.eu-central-1.amazonaws.com/dev/user/readitem',
          {
            "IDs": [authResponse.data.ID]
          },
          {
            headers: {
              'Authorization': authToken
            }
          }
        );

        // Set the user data in state
        setUserData(response.data);
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };

    fetchData();
  }, [cookies.email, cookies.password]); // Run this effect whenever email or password in cookies change

  return (
    <div className="UserPage">
      <h1>User Information</h1>
      {userData && (
        <div className="userData">
          {Object.keys(userData).map(key => (
            <div key={key}>
              <strong>{key}:</strong> {userData[key]}
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default UserPage;
